import { ProgramMetadata } from "@gear-js/api";
import { Button } from "@gear-js/ui";
import { useState } from "react";
import { useApi, useAlert } from "@gear-js/react-hooks";
import { AnyJson } from "@polkadot/types/types";

function ReadState() {
  const { api } = useApi();

  const alert = useAlert();

  const [fullState, setFullState] = useState<AnyJson>();

     // Add your programID
     const programIDFT =
     "0x7219d9919acfb959e5f5ed25e3a1ba3816537be0ec9c91ac1d5b20ea6da250a0";
  
    // Add your metadata.txt
   const meta =
   ""

  const metadata = ProgramMetadata.from(meta);


  console.log(fullState);
  

  
  const getState = () => {

    api.programState
      .read({ programId: programIDFT,payload:"" }, metadata)
      .then((result) => {
        setFullState(result.toJSON());
        alert.success("Successful state");
      })
      .catch(({ message }: Error) => alert.error(message));
  };

  

  return (
    <div className="text-black">
      <div>Full State</div>
      <div className="state">
        State
        <p className="text"> {JSON.stringify(fullState)}</p>
      </div>
      <Button text="Get Full State" onClick={getState} />
    </div>
  );
}

export { ReadState };
